INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES	
(53750, 0, 0, 'Thank you, Stonemother.', 12, 0, 100, 1, 0, 24690, 0, 'Aggra to Player'),	
(53750, 1, 0, 'Quickly, friend. Go\'el should be just below.', 12, 0, 100, 1, 0, 24691, 0, 'Aggra to Player'),	
(53750, 2, 0, 'There\'s too many of them! I don\'t know how long we can hold out! Go\'el, now would be a good time to wake up!', 12, 0, 100, 5, 0, 24696, 0, 'Aggra to Player'),	
(53750, 3, 0, 'Did he just...? Go\'el, can you hear me? We need your help! Do something!', 12, 0, 100, 6, 0, 24697, 0, 'Aggra to Player'),	
(53750, 4, 0, 'GO\'EL! Pull it together or we\'re all going to DIE!', 12, 0, 100, 22, 0, 24698, 0, 'Aggra to Player'),	
(53750, 5, 0, 'He did NOT just say that!', 12, 0, 100, 274, 0, 24699, 0, 'Aggra to Player'),	
(53750, 6, 0, 'Damn you, Go\'el - WAKE UP! Don\'t let it end like this! We\'ve come too far to fail now!', 12, 0, 100, 5, 0, 24700, 0, 'Aggra to Player'),	
(53750, 7, 0, 'Oh, don\'t you tell ME to be patient, you green-skinned...', 12, 0, 100, 14, 0, 24701, 0, 'Aggra to Player'),	
(53750, 8, 0, 'You\'re almost home, my love. Our time is so brief. You will change again, Go\'el - but I will find you! I\'ll NEVER give up on you!', 12, 0, 100, 1, 0, 24702, 0, 'Aggra to Player'),	
(53750, 9, 0, 'Fire. My love has become fire. I feared this. To find him again, we must enter Ragnaros\' burning domain.', 12, 0, 100, 1, 0, 24703, 0, 'Aggra to Player');
